sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code below
	var vs = current.vehicle_status; // create a variable for the current vehicle status field

	var gr = new GlideRecord('x_1313534_lva_loaner_vehicle_catalog'); // create a variable for the new GlideRecord on the loaner vehicle catalog table

	gr.addQuery('sys_id', current.vehicle); // query that table for the sys_id of the current vehicle field
	gr.query(); // execute the query

	if (gr.next()) // .next means that we will be making changes to the record that was queried above

	// iterate through the vehicle tracker table
	if (vs == 'Pending Release' || vs == 'Out on Field' || vs == 'Returned for Inspection' || vs == 'Sent for Servicing') {
		gr.status = 'Unavailable'; // set the status field of the loaner vehicle catalog to unavailable 
	}

	else if (vs == 'Returned to Warehouse') {
		gr.status = 'Available'; //set the status field of the loaner vehicle catalog to available
	}

	else if (vs == 'Decommissioned') {
		gr.status = 'Decommissioned'; // set the status field of the loaner vehicle catalog to decomissioned
	}

	gr.update(); // save the changes

})(current, previous);
