sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    // Add your code here
    var vehicleServicingRecord = new GlideRecord('x_1313534_lva_vehicle_servicing');

    // Query the servicing table for the current sys_id of the record that is in the vehicle tracker field
    vehicleServicingRecord.addQuery('vehicle_servicing', current.sys_id);

    // Query the servicing table for records where the tracker status is open
    vehicleServicingRecord.addQuery('tracker_status', 'open');

    // Execute the query
    vehicleServicingRecord.query();

    // If no matching records are found from the previous query, create a new record
    if (!vehicleServicingRecord._next()) {
        var newServicingRecord = new GlideRecord('x_1313534_lva_vehicle_servicing');

        // Initialize the creation of a new record on the servicing table
        newServicingRecord.initialize();

        // Set values for the new record
        newServicingRecord.setValue('vehicle_servicing', current.sys_id);
        newServicingRecord.setValue('vehicle', current.vehicle);
        newServicingRecord.setValue('vehicle_status', 'In Service');
        newServicingRecord.setValue('ticket_status', 'open');

        // Get the current date and set it in the record
        var currentDate = new GlideDate();
        newServicingRecord.setValue('date_sent_for_servicing', currentDate);

        // Insert the new record into the database
        newServicingRecord.insert();
    }

})(current, previous);
